import React, {useState, useEffect } from 'react';
import Chart from 'react-google-charts';
import { CONFIG } from '../../../../config/constant';
import {Row, Col, Card, Table, Tab, Nav, Form, InputGroup, FormControl } from 'react-bootstrap';
import BTable from 'react-bootstrap/Table';
import axios from 'axios';

//API Configuration
///Import for API Keys from Config
const secretKey = CONFIG.binance.secretKey;
const apiKey = CONFIG.binance.apiKey;
//Binance API
const Binance = require('node-binance-api');
const binance = new Binance().options({
  APIKEY: apiKey,
  APISECRET: secretKey
});
binance.options({
  APIKEY: apiKey,
  APISECRET: secretKey,
  useServerTime: true,
  recvWindow: 5000, // Set a higher recvWindow to increase response timeout
  verbose: true, // Add extra output when subscribing to WebSockets, etc
  log: log => {
    console.log(log); // You can create your own logger here, or disable console output
  }
});

//Create data for line chart
function createBarData(data) {
    const lineData = [
        ['Date', 'Price']
    ];

    for (const day in data) {
        console.log(data[day])
    }
    return lineData;
}

const MarketsPriceLineAreaChart = () => {

    const [ priceData, setPriceData ] = useState([]);

    const url = 'https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=365&interval=daily';

    const getPriceData = async () => {
        axios
            .get(
                url
            )
            .then(res => {
                setPriceData(res.data);
            })
            .catch(error => console.log(error));
        }
    
      useEffect(() => {
          getPriceData()
    
          const interval=setInterval(() => {
              getPriceData()
          }, 60000)
    
          console.log('PRICEDATA',priceData)
    
          return() => clearInterval(
              interval
              )
          }, []
      );


    return <Chart
        width='100%'
        height='300px'
        chartType="AreaChart"
        loader={<div>Loading Chart</div>}
        data={[
            ['Year', 'Sales', 'Expenses'],
            ['2013', 1000, 400],
            ['2014', 1170, 460],
            ['2015', 660, 1120],
            ['2016', 1030, 540],
        ]}
        options={{
            title: 'Company Performance',
            hAxis: { title: 'Year', titleTextStyle: { color: '#333' } },
            vAxis: { minValue: 0 },
            colors: ['#1de9b6', '#1dc4e9']
        }}
        rootProps={{ 'data-testid': '1' }}
    />;
}

export default MarketsPriceLineAreaChart;
