import React, { useContext } from 'react';
import { Link } from "react-router-dom";
import PerfectScrollbar from 'react-perfect-scrollbar';

import { ConfigContext } from "../../../../../contexts/ConfigContext";
import * as actionType from "../../../../../store/actions";

import navImage1 from '../../../../../assets/images/nav-bg/navbar-img-1.jpg';
import navImage2 from '../../../../../assets/images/nav-bg/navbar-img-2.jpg';
import navImage3 from '../../../../../assets/images/nav-bg/navbar-img-3.jpg';
import navImage4 from '../../../../../assets/images/nav-bg/navbar-img-4.jpg';
import navImage5 from '../../../../../assets/images/nav-bg/navbar-img-5.jpg';

import backImage1 from '../../../../../assets/images/bg-images/bg1.jpg';
import backImage3 from '../../../../../assets/images/bg-images/bg3.jpg';
import backImage4 from '../../../../../assets/images/bg-images/bg4.jpg';
import backImage5 from '../../../../../assets/images/bg-images/bg5.jpg';

import patternImage1 from '../../../../../assets/images/bg-images/1.png';
import patternImage2 from '../../../../../assets/images/bg-images/2.png';
import patternImage3 from '../../../../../assets/images/bg-images/3.png';
import patternImage4 from '../../../../../assets/images/bg-images/4.png';
import patternImage5 from '../../../../../assets/images/bg-images/5.png';
import patternImage6 from '../../../../../assets/images/bg-images/6.png';

const ColorOptions = () => {
    const configContext = useContext(ConfigContext);
    const { layout, subLayout, headerBackColor, navBrandColor, navBackImage, layout6Background, navBackColor } = configContext.state;
    const { dispatch } = configContext;

    let colorOptions = '';
    let colorOptionsWithoutHorizontal = '';

    const onChangeNavBrandColor = brand => {
        dispatch({type: actionType.NAV_BRAND_COLOR, navBrandColor: brand});
    };

    const onChangeNavBackImage = image => {
        dispatch({type: actionType.NAV_BACK_IMAGE, navBackImage: image});
    };

    const onChangeLayout6Background = backgound => {
        dispatch({type: actionType.LAYOUT6_BACKGROUND, layout6Background: backgound});
    };

    const onChangeHeaderBackColor = backgound => {
        dispatch({type: actionType.HEADER_BACK_COLOR, headerBackColor: backgound});
    };

    const onChangeNavBackColor = backgound => {
        dispatch({type: actionType.NAV_BACK_COLOR, navBackColor: backgound});
    };

    if (subLayout !== 'layout-6' && layout !== 'horizontal') {
        colorOptionsWithoutHorizontal = (
            <div>
                <h6>Menu Brand Color</h6>
                <div className="theme-color brand-color">
                    <Link to='#' onClick={() => onChangeNavBrandColor('brand-default')} className={navBrandColor === 'brand-default' ? 'active' : ''} data-value="brand-default"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBrandColor('brand-blue')} className={navBrandColor === 'brand-blue' ? 'active' : ''} data-value="brand-blue"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBrandColor('brand-red')} className={navBrandColor === 'brand-red' ? 'active' : ''} data-value="brand-red"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBrandColor('brand-purple')} className={navBrandColor === 'brand-purple' ? 'active' : ''} data-value="brand-purple"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBrandColor('brand-lightblue')} className={navBrandColor === 'brand-lightblue' ? 'active' : ''} data-value="brand-lightblue"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBrandColor('brand-dark')} className={navBrandColor === 'brand-dark' ? 'active' : ''} data-value="brand-dark"><span/><span/></Link>
                </div>
                <h6>Navbar Image</h6>
                <div className="theme-color navbar-images">
                    <Link to='#' onClick={() => onChangeNavBackImage('navbar-image-1')} className={navBackImage === 'navbar-image-1' ? 'active' : ''} style={{backgroundImage: `url(${navImage1})`}} data-value="navbar-image-1"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBackImage('navbar-image-2')} className={navBackImage === 'navbar-image-2' ? 'active' : ''} style={{backgroundImage: `url(${navImage2})`}} data-value="navbar-image-2"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBackImage('navbar-image-3')} className={navBackImage === 'navbar-image-3' ? 'active' : ''} style={{backgroundImage: `url(${navImage3})`}} data-value="navbar-image-3"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBackImage('navbar-image-4')} className={navBackImage === 'navbar-image-4' ? 'active' : ''} style={{backgroundImage: `url(${navImage4})`}} data-value="navbar-image-4"><span/><span/></Link>
                    <Link to='#' onClick={() => onChangeNavBackImage('navbar-image-5')} className={navBackImage === 'navbar-image-5' ? 'active' : ''} style={{backgroundImage: `url(${navImage5})`}} data-value="navbar-image-5"><span/><span/></Link>
                </div>
            </div>
        );
    }

    if (subLayout === 'layout-6') {
        colorOptions = (
            <div>
                <h6>Background Color</h6>
                <div className="theme-color laybg-color small">
