//IMPORTS 
///Settings/Configuration Imports
import { CONFIG } from '../../../../config/constant';
///Library Imports
import React, {useState, useEffect } from 'react';
import Chart from 'react-google-charts';
///Data/API Imports
import axios from 'axios';

// //CONFIGURATION
// ///Binance API Configuration
// const Binance = require('node-binance-api');
// const binance = new Binance().options(CONFIG.binance);

//FUNCTIONS
//Create data for line chart
function createBarData(data, dataTwo) {
    const lineData = [
        ['Date', 'Bitcoin Price']
    ];

    let prices = data['prices'];
    let pricesTwo = dataTwo['prices'];

    for (const day in prices) {
        // console.log('Daily Data', prices[day])
        let newDate = new Date(prices[day][0])
        prices[day][0] = newDate;
        lineData.push(prices[day]);
    };

    console.log('DAILY PRICES', lineData);

    return lineData;
}

const MarketsPriceLineAreaChart = () => {

    //Take static list of Tickers

    const [ priceDataOne, setPriceDataOne ] = useState([]);
    const [ priceDataTwo, setPriceDataTwo ] = useState([]);
    const [ securityList, setSecurityList ] = useState([]);

    setSecurityList(
        [
            'bitcoin',
            'ethereum',
            'dogecoin'
        ]
    );
    
    const url = 'https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=365&interval=daily';
    const urlTwo = 'https://api.coingecko.com/api/v3/coins/ethereum/market_chart?vs_currency=usd&days=365&interval=daily';

    const getPriceDataOne = async () => {
        axios
            .get(
                url
            )
            .then(res => {
                setPriceDataOne(res.data);
            })
            .catch(error => console.log(error));
    };
    
    useEffect(() => {
        getPriceDataOne()

        const interval=setInterval(() => {
            getPriceDataOne()
        }, 60000)

        // console.log('PRICEDATA',priceData)

        return() => clearInterval(
            interval
            )
        }, []
    );

    const getPriceDataTwo = async () => {
        axios
            .get(
                urlTwo
            )
            .then(res => {
                setPriceDataTwo(res.data);
            })
            .catch(error => console.log(error));
    };
    
    useEffect(() => {
        getPriceDataTwo()

        const interval=setInterval(() => {
            getPriceDataTwo()
        }, 60000)

        // console.log('PRICEDATA',priceData)

        return() => clearInterval(
            interval
            )
        }, []
    );

    const data = createBarData(priceDataOne, priceDataTwo);

    return (
        <React.Fragment>
            {/* <Select
                defaultValue={[colourOptions[2], colourOptions[3]]}
                isMulti
                name="colors"
                options={colourOptions}
                className="basic-multi-select"
                classNamePrefix="select"
                isSearchable
            /> */}
            <Chart
                width='100%'
                height='300px'
                chartType="AreaChart"
                loader={<div>Loading Chart</div>}
                // data={[
                //     ['Year', 'Sales', 'Expenses'],
                //     ['2013', 1000, 400],
                //     ['2014', 1170, 460],
                //     ['2015', 660, 1120],
                //     ['2016', 1030, 540],
                // ]}
                data={ data }
                options={{
                    title: 'Price Performance',
                    hAxis: { title: 'Year', titleTextStyle: { color: '#333' } },
                    vAxis: { minValue: 0 },
                    colors: ['#1de9b6', '#1dc4e9']
                }}
                rootProps={{ 'data-testid': '1' }}
            />
        </React.Fragment>
    );
}

export default MarketsPriceLineAreaChart;
