import React, {useState, useEffect } from 'react';
import Chart from 'react-google-charts';
import { CONFIG } from '../../../../config/constant';
import {Row, Col, Card, Table, Tab, Nav, Form, InputGroup, FormControl } from 'react-bootstrap';
import BTable from 'react-bootstrap/Table';
import axios from 'axios';

//API Configuration
///Import for API Keys from Config
const secretKey = CONFIG.binance.secretKey;
const apiKey = CONFIG.binance.apiKey;
//Binance API
const Binance = require('node-binance-api');
const binance = new Binance().options({
  APIKEY: apiKey,
  APISECRET: secretKey
});
binance.options({
  APIKEY: apiKey,
  APISECRET: secretKey,
  useServerTime: true,
  recvWindow: 5000, // Set a higher recvWindow to increase response timeout
  verbose: true, // Add extra output when subscribing to WebSockets, etc
  log: log => {
    console.log(log); // You can create your own logger here, or disable console output
  }
});

//Create data for line chart
function createBarData(data, dataTwo) {
    const lineData = [
        ['Date', 'Bitcoin Price']
    ];

    let prices = data['prices'];
    let pricesTwo = dataTwo['prices'];

    for (const day in prices) {
        // console.log('Daily Data', prices[day])
        let newDate = new Date(prices[day][0])
        prices[day][0] = newDate;
        lineData.push(prices[day]);
    };

    console.log('DAILY PRICES', lineData);

    return lineData;
}

const MarketsPriceLineAreaChart = () => {

    const [ priceDataOne, setPriceDataOne ] = useState([]);
    const [ priceDataTwo, setPriceDataTwo ] = useState([]);

    const url = 'https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=365&interval=daily';
    const urlTwo = 'https://api.coingecko.com/api/v3/coins/ethereum/market_chart?vs_currency=usd&days=365&interval=daily';

    const getPriceDataOne = async () => {
        axios
            .get(
                url
            )
            .then(res => {
                setPriceDataOne(res.data);
            })
            .catch(error => console.log(error));
    };
    
    useEffect(() => {
        getPriceDataOne()

        const interval=setInterval(() => {
            getPriceDataOne()
        }, 60000)

        // console.log('PRICEDATA',priceData)

        return() => clearInterval(
            interval
            )
        }, []
    );

    const getPriceDataTwo = async () => {
        axios
            .get(
                urlTwo
            )
            .then(res => {
                setPriceDataTwo(res.data);
            })
            .catch(error => console.log(error));
    };
    
    useEffect(() => {
        getPriceDataTwo()

        const interval=setInterval(() => {
            getPriceDataTwo()
        }, 60000)

        // console.log('PRICEDATA',priceData)

        return() => clearInterval(
            interval
            )
        }, []
    );

    const data = createBarData(priceDataOne, priceDataTwo);

    return (
        <React.Fragment>
            <Chart
                width='100%'
                height='300px'
                chartType="AreaChart"
                loader={<div>Loading Chart</div>}
                // data={[
                //     ['Year', 'Sales', 'Expenses'],
                //     ['2013', 1000, 400],
                //     ['2014', 1170, 460],
                //     ['2015', 660, 1120],
                //     ['2016', 1030, 540],
                // ]}
                data={ data }
                options={{
                    title: 'Price Performance',
                    hAxis: { title: 'Year', titleTextStyle: { color: '#333' } },
                    vAxis: { minValue: 0 },
                    colors: ['#1de9b6', '#1dc4e9']
                }}
                rootProps={{ 'data-testid': '1' }}
            />
        </React.Fragment>
    );
}

export default MarketsPriceLineAreaChart;
